import React, { useState, useEffect, useRef } from 'react';
import { Mic, MicOff, Send, Volume2, VolumeX, Bot, RotateCcw } from 'lucide-react';
import MessageList from './components/MessageList';
import useChat from './hooks/useChat';
import useTextToSpeech from './hooks/useTextToSpeech';
import useAudioRecorder from './hooks/useAudioRecorder';
import './index.css';

const App = () => {
  // Generate time-appropriate greeting
  const getTimeGreeting = () => {
    const hour = new Date().getHours();
    if (hour < 12) return 'Good morning';
    if (hour < 17) return 'Good afternoon';
    return 'Good evening';
  };

  const [messages, setMessages] = useState([
    {
      id: 'welcome',
      text: `Hello! ${getTimeGreeting()}! I'm your AI assistant powered by news and current events. How can I help you today?`,
      sender: 'bot',
      timestamp: new Date(),
      isVoice: false
    }
  ]);
  const [isMuted, setIsMuted] = useState(false);
  const [isTyping, setIsTyping] = useState(false);
  const [inputText, setInputText] = useState('');
  const [isStreaming, setIsStreaming] = useState(false);
  const [streamingText, setStreamingText] = useState('');

  const messagesEndRef = useRef(null);

  const { speak, stopSpeaking, isSpeaking } = useTextToSpeech();
  const { 
    startRecording: startAudioRecording, 
    stopRecording: stopAudioRecording, 
    isRecording: isAudioRecording, 
    audioBlob, 
    error: audioError,
    clearAudio 
  } = useAudioRecorder();

  const { 
    sendMessage, 
    sendStreamingMessage,
    getChatHistory,
    clearChatHistory,
    resetSession,
    sessionId,
    chatHistory,
    isLoading, 
    error: chatError 
  } = useChat();

  // Determine which recording mode we're in
  const isVoiceMode = isAudioRecording;
  const isProcessing = isLoading || isStreaming;

  // Scroll to bottom when new messages arrive
  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages, streamingText]);

  // Load chat history when session is available
  useEffect(() => {
    if (sessionId) {
      loadChatHistory();
    }
  }, [sessionId]);

  const loadChatHistory = async () => {
    try {
      const history = await getChatHistory();
      if (history && history.length > 0) {
        const formattedMessages = history.map(msg => ({
          id: msg.id,
          text: msg.text,
          sender: msg.sender,
          timestamp: new Date(msg.timestamp),
          isVoice: false
        }));
        setMessages(formattedMessages);
      }
    } catch (error) {
      console.error('Failed to load chat history:', error);
    }
  };

  const handleVoiceToggle = () => {
    if (isVoiceMode) {
      stopAudioRecording();
    } else {
      setInputText(''); // Clear input when starting recording
      startAudioRecording();
    }
  };

  const handleMuteToggle = () => {
    if (isSpeaking) {
      stopSpeaking();
    }
    setIsMuted(!isMuted);
  };

  const handleResetChat = async () => {
    try {
      if (sessionId) {
        await clearChatHistory();
      }
      resetSession();
      setMessages([
        {
          id: 'welcome',
          text: `Hello! ${getTimeGreeting()}! I'm your AI assistant powered by news and current events. How can I help you today?`,
          sender: 'bot',
          timestamp: new Date(),
          isVoice: false
        }
      ]);
    } catch (error) {
      console.error('Failed to reset chat:', error);
    }
  };

  const handleSendMessage = async () => {
    if (!inputText.trim() || isProcessing) return;

    const userMessage = {
      id: Date.now().toString(),
      text: inputText.trim(),
      sender: 'user',
      timestamp: new Date(),
      isVoice: false
    };

    setMessages(prev => [...prev, userMessage]);
    const messageText = inputText.trim();
    setInputText('');

    try {
      setIsTyping(true);
      setIsStreaming(true);
      setStreamingText('');

      // Use streaming for better UX
      await sendStreamingMessage(
        messageText,
        // onChunk
        (chunk) => {
          setStreamingText(prev => prev + chunk);
        },
        // onComplete
        (completedSessionId) => {
          const botMessage = {
            id: (Date.now() + 1).toString(),
            text: streamingText,
            sender: 'bot',
            timestamp: new Date(),
            isVoice: false
          };
          setMessages(prev => [...prev, botMessage]);
          setStreamingText('');
          setIsStreaming(false);
          setIsTyping(false);

          // Speak the response if not muted
          if (!isMuted && streamingText) {
            speak(streamingText);
          }
        },
        // onError
        (error) => {
          console.error('Streaming error:', error);
          const errorMessage = {
            id: (Date.now() + 1).toString(),
            text: `Error: ${error}`,
            sender: 'bot',
            timestamp: new Date(),
            isVoice: false
          };
          setMessages(prev => [...prev, errorMessage]);
          setIsStreaming(false);
          setIsTyping(false);
          setStreamingText('');
        }
      );
    } catch (error) {
      console.error('Error sending message:', error);
      const errorMessage = {
        id: (Date.now() + 1).toString(),
        text: `Error: ${chatError || 'Failed to send message'}`,
        sender: 'bot',
        timestamp: new Date(),
        isVoice: false
      };
      setMessages(prev => [...prev, errorMessage]);
      setIsStreaming(false);
      setIsTyping(false);
      setStreamingText('');
    }
  };

  // Handle sending voice message when audio is ready
  useEffect(() => {
    if (audioBlob && !isAudioRecording) {
      handleSendVoiceMessage(audioBlob);
    }
  }, [audioBlob, isAudioRecording]);

  const handleSendVoiceMessage = async (audioBlob) => {
    try {
      // For now, we'll just send a placeholder message
      // In a real implementation, you'd transcribe the audio first
      const userMessage = {
        id: Date.now().toString(),
        text: 'Voice message (transcription not implemented)',
        sender: 'user',
        timestamp: new Date(),
        isVoice: true
      };

      setMessages(prev => [...prev, userMessage]);
      clearAudio();

      // Send a text message instead
      await handleSendMessage();
    } catch (error) {
      console.error('Error sending voice message:', error);
      clearAudio();
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100 flex items-center justify-center p-4">
      <div className="w-full max-w-4xl">
        {/* Header */}
        <div className="bg-white rounded-t-2xl shadow-lg p-6 border-b">
          <div className="flex items-center justify-between">
            <div className="flex items-center space-x-3">
              <div className="w-12 h-12 bg-gradient-to-r from-blue-500 to-purple-600 rounded-full flex items-center justify-center">
                <Bot className="w-6 h-6 text-white" />
              </div>
              <div>
                <h1 className="text-2xl font-bold text-gray-800">AI News Assistant</h1>
                <div className="flex items-center space-x-2">
                  <div className="w-2 h-2 rounded-full bg-green-500"></div>
                  <span className="text-sm text-gray-600">
                    {sessionId ? `Session: ${sessionId.slice(0, 8)}...` : 'Ready'}
                  </span>
                </div>
              </div>
            </div>

            <div className="flex items-center space-x-2">
              <button
                onClick={handleResetChat}
                className="p-2 rounded-full bg-gray-100 text-gray-600 hover:bg-gray-200 transition-colors"
                title="Reset Chat"
              >
                <RotateCcw className="w-5 h-5" />
              </button>
              <button
                onClick={handleMuteToggle}
                className={`p-2 rounded-full transition-colors ${
                  isMuted ? 'bg-red-100 text-red-600' : 'bg-gray-100 text-gray-600 hover:bg-gray-200'
                }`}
                title={isMuted ? 'Unmute' : 'Mute'}
              >
                {isMuted ? <VolumeX className="w-5 h-5" /> : <Volume2 className="w-5 h-5" />}
              </button>
            </div>
          </div>
        </div>

        {/* Chat Container */}
        <div className="bg-white rounded-b-2xl shadow-lg h-96 flex flex-col">
          {/* Messages */}
          <div className="flex-1 overflow-y-auto p-6">
            <MessageList 
              messages={messages} 
              isTyping={isTyping}
              streamingText={streamingText}
              isStreaming={isStreaming}
            />
            <div ref={messagesEndRef} />
          </div>

          {/* Input Area */}
          <div className="border-t p-6">
            <div className="flex items-center space-x-4">
              {/* Voice Recording Button */}
              <button
                onClick={handleVoiceToggle}
                className={`p-4 rounded-full transition-all duration-200 ${
                  isVoiceMode 
                    ? 'bg-red-500 text-white animate-pulse shadow-lg' 
                    : 'bg-blue-500 text-white hover:bg-blue-600 shadow-md'
                }`}
                disabled={isProcessing}
                title="Voice Recording"
              >
                {isVoiceMode ? <MicOff className="w-6 h-6" /> : <Mic className="w-6 h-6" />}
              </button>

              {/* Text Input */}
              <div className="flex-1">
                <input
                  type="text"
                  value={inputText}
                  onChange={(e) => setInputText(e.target.value)}
                  placeholder={
                    isVoiceMode ? "Recording... Click mic to stop" :
                    isProcessing ? "Processing..." :
                    "Ask me about current news and events..."
                  }
                  className="w-full px-4 py-3 border border-gray-300 rounded-full focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                  onKeyPress={(e) => {
                    if (e.key === 'Enter' && !isProcessing) {
                      handleSendMessage();
                    }
                  }}
                  disabled={isVoiceMode || isProcessing}
                />
              </div>

              {/* Send Button */}
              <button
                className={`p-3 rounded-full transition-colors disabled:opacity-50 ${
                  isVoiceMode 
                    ? 'bg-red-500 text-white hover:bg-red-600' 
                    : 'bg-blue-500 text-white hover:bg-blue-600'
                }`}
                disabled={!inputText.trim() || isProcessing}
                onClick={handleSendMessage}
                title="Send message"
              >
                <Send className="w-5 h-5" />
              </button>
            </div>

            {/* Loading indicator */}
            {isProcessing && (
              <div className="mt-4 text-center text-gray-500">
                <div className="inline-flex items-center space-x-2">
                  <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-blue-500"></div>
                  <span>Processing...</span>
                </div>
              </div>
            )}

            {/* Status Messages */}
            {isVoiceMode && (
              <div className="mt-3 text-center">
                <p className="text-sm text-red-600 flex items-center justify-center space-x-2">
                  <Mic className="w-4 h-4" />
                  <span>Recording... Speak now</span>
                </p>
              </div>
            )}

            {isTyping && (
              <div className="mt-3 text-center">
                <p className="text-sm text-gray-600 flex items-center justify-center space-x-2">
                  <Bot className="w-4 h-4" />
                  <span>Assistant is typing...</span>
                </p>
              </div>
            )}

            {/* Error Messages */}
            {chatError && (
              <div className="mt-3 text-center">
                <p className="text-sm text-red-600">
                  Error: {chatError}
                </p>
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default App;
